import React,{useEffect,useState} from 'react';

import {AppBar ,Typography,Grow,Grid, Container} from '@material-ui/core';
import { getPosts } from './actions/posts';
import Posts from './components/folder2/Posts';
import Form from './components/folder1/Form';
import useStyles from './Styles';
import img from'./images/img.jpg';
import { useDispatch } from 'react-redux';





const App = () => {
  const [currentId,setCurrentId]=useState(null);
  const classes = useStyles();
   const dispatch= useDispatch();
  
   useEffect(()=> {
          dispatch(getPosts());

   }, [currentId,dispatch]);



  return (
    <Container maxwidth="">
<AppBar className={classes.appBar} position='static' color='inherit'>
      <Typography className={classes.heading} variant='h2' align='center'> memories</Typography>
        <img className={classes.image} src ={img} alt='img' height='60'/>
</AppBar>
<Grow in>
  <Container>
    <Grid container   flexDirection="colomn-reverse" justify="space-between" alignItems='stretch' spacing='3' >
      <Grid item xs={12} sm={7}>
        <Posts setCurrentId={setCurrentId} />
      </Grid>

      <Grid item xs={12} sm={4}>
         <Form currentId={currentId} setCurrentId={setCurrentId}/>
     </Grid>
    </Grid>
  </Container>

</Grow>

    </Container>
      
       
  );
}

export default App;
